import React, { useState } from "react";




const FilterButton = ({ complianceTypes, onFilterChange }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedFilter, setSelectedFilter] = useState("");

  const handleFilterClick = (filter) => {
    setSelectedFilter(filter);
    onFilterChange(filter);
    setIsOpen(false);
  };

  return (
    <div className="filter-button">
      <button onClick={() => setIsOpen(!isOpen)}>
        {selectedFilter || "Compliance Type"}
        <i className={`fa fa-caret-${isOpen ? "up" : "down"}`} />
      </button>
      {isOpen && (
        <ul>
          {complianceTypes.map((type) => (
            <li key={type} onClick={() => handleFilterClick(type)}>
              {type}
            </li>
          ))}
          <li onClick={() => handleFilterClick("Date")}>Date</li>
          <li onClick={() => handleFilterClick("Month")}>Month</li>
          <li onClick={() => handleFilterClick("Life")}>Life</li>
          <li onClick={() => handleFilterClick("Line")}>Line</li>
        </ul>
      )}
    </div>
  );
};


export default FilterButton;
